ProbeAction.ScriptBody:
#================================================================================= # Script to gather ADFS.2016.alerts and close via on demand task # # Author: Kevin Justin # v1.0 #=================================================================================
# Constants section - modify stuff here: #================================================================================= # Assign script name variable for use in event logging. # ScriptName should be the same as the ID of the module that the script is contained in $ScriptName = "Microsoft.Active.Directory.Federation.Services.2016.SummaryReport.Script.DataSource.v1015.ps1" $EventID = "590" #=================================================================================
# Starting Script section - All scripts get this #================================================================================= # Gather the start time of the script $StartTime = Get-Date #Set variable to be used in logging events $whoami = whoami # Load MOMScript API $momapi = New-Object -comObject MOM.ScriptAPI #Log script event that we are starting task $momapi.LogScriptEvent($ScriptName,$EventID,0,"`n Script is starting. `n Running as ($whoami).") #=================================================================================
# PropertyBag Script section - Monitoring scripts get this #================================================================================= # Load SCOM PropertyBag function $bag = $momapi.CreatePropertyBag() #=================================================================================
# Begin MAIN script section #=================================================================================
#For a simple example - get files in Windows Temp: $date = get-date -uFormat "%Y-%m-%d"
$momapi.LogScriptEvent($ScriptName,$EventID,0,"`n Gather ADFS 2016+ services Alert report for date ($date).")
$DSMessage = "Proactive DailyTasks - Daily Summary ADFS 2016+ Alerts for date ($date)."
# Grab past 24 hours if ( (get-date).DayOfWeek -ne "Monday" ) { $Time = [DateTime](Get-Date).AddDays(-1) } if ( (get-date).DayOfWeek -eq "Monday" ) { $Time = [DateTime](Get-Date).AddDays(-3) }
$momapi.LogScriptEvent($ScriptName,$EventID,0,"`n Gather ADFS 2016+ Alerts for date ($date).")
# Gather ADFS 2016+ alerts # Method logic provided by Andrew Bradley ## Grab the MP, get the Monitors and Rules from the MP, then grab all alerts found inside the Monitors/Rules $SCOMCoreMP = Get-SCOMManagementPack -DisplayName "Active Directory Federation Services" $SCOMCoreRules = $SCOMCoreMP.GetRules() $SCOMCoreMonitors = $SCOMCoreMP.GetMonitors()
$SCOMCoreReportAlerts = Get-SCOMAlert | ? { ($_.Name -in $SCOMCoreRules.DisplayName) -or ($_.Name -in $SCOMCoreMonitors.DisplayName) } $SCOMCoreReportAlerts.Count
$SCOMCoreRuleAlerts = Get-SCOMAlert | ? { ( $_.Name -in $SCOMCoreRules.DisplayName) -AND ( $_.ResolutionState -ne "255" ) } $SCOMCoreRuleAlerts.Count
$SCOMCoreMonitorAlerts = Get-SCOMAlert | ? { ($_.Name -in $SCOMCoreMonitors.DisplayName ) -AND ( $_.ResolutionState -ne "255" ) } $SCOMCoreMonitorAlerts.Count # | ? { $_.TimeRaised -ge $Time }
#$Test = $ADFSAlerts.Count $Test = $SCOMCoreReportAlerts.Count
# Open ADFS server alerts since last report $ADFSAlertsSinceReport = $SCOMCoreReportAlerts | ? { $_.TimeRaised -gt $Time } $ADFSAlertsSinceReport $ADFSAlertsSinceReport.Count
# Check for open alerts older than reporting period $OpenAlerts = $SCOMCoreReportAlerts | ? { $_.ResolutionState -ne 255 } | ? { $_.TimeRaised -ge $Time } $OpenAlertDetail = $OpenAlerts | ft TimeRaised,NetbiosComputerName,Name -auto
$OpenADFSAlertsSinceReport = $ADFSAlertsSinceReport | ? { $_.ResolutionState -ne 255 } $OpenADFSAlertsSinceReport.Count
# Alerts closed within report period $Time $ClosedAlerts = $SCOMCoreReportAlerts | ? { $_.ResolutionState -eq 255 } | ? { $_.TimeRaised -ge $Time } $ClosedAlertDetail = $ClosedAlerts | ft TimeRaised,NetbiosComputerName,Name -auto
$Alerts = $SCOMCoreReportAlerts | ft TimeRaised,NetbiosComputerName,Name -auto -wrap
$AlertSorted = @()
foreach ( $Name in $($ADFSAlerts.Name | sort -uniq) ) { $AlertSorted += $Name $AlertSorted += $SCOMCoreReportAlerts | ? { $_.Name -eq $Name } | ft TimeRaised,NetbiosComputerName,Name -auto $AlertSorted += "#=====================================" }
$AlertSorted
# Break out ADFS alerts # MEX endpoint $ADFSMEX = $OpenADFSAlertsSinceReport | ? { $_.Name -like "*MEX endpoint*" } $ADFSMEX.Count $ClosedADFSMEX = $ClosedAlerts | ? { $_.Name -like "*MEX endpoint*" } $ClosedADFSMEX.Count
# SQL $ADFSSQL = $OpenADFSAlertsSinceReport | ? { $_.Name -like "SQL*" } $ADFSSQL.Count $ClosedADFSSQL = $ClosedAlerts | ? { $_.Name -like "SQL*" } $ClosedADFSSQL.Count
# Token $ADFSToken = $OpenADFSAlertsSinceReport | ? { $_.Name -like "Token*" } $ADFSToken.Count $ClosedADFSToken = $ClosedAlerts | ? { $_.Name -like "Token*" } $ClosedADFSToken.Count
# SAML $ADFSSAML = $OpenADFSAlertsSinceReport | ? { $_.Name -like "*SAML*" } $ADFSSAML.Count $ClosedADFSSAML = $ClosedAlerts | ? { $_.Name -like "*SAML*" } $ClosedADFSSAML.Count
# FsProxy $ADFSFsProxy = $OpenADFSAlertsSinceReport | ? { $_.Name -like "*FsProxy*" } $ADFSFsProxy.Count $ClosedADFSFsProxy = $ClosedAlerts | ? { $_.Name -like "*FsProxy*" } $ClosedADFSFsProxy.Count
# Build Report $ADFSMessage = @()
if ( $ADFSAlerts.count -eq 0 ) { $ADFSMessage += "" $ADFSMessage += "ZERO Active Directory Federation Services 2016 alerts for ($date)." $ADFSMessage += "" }
if ( $ADFSAlerts.count -gt 0 ) { $ADFSMessage += "" $ADFSMessage += $DSMessage $ADFSMessage += "" $ADFSMessage += "#========================================================" $ADFSMessage += "#" $ADFSMessage += "# Total ADFS2016+ alerts = $($SCOMCoreReportAlerts.Count)" $ADFSMessage += "#" $ADFSMessage += "# OPEN alerts since last run = $($OpenADFSAlertsSinceReport.Count)" $ADFSMessage += "# CLOSED alerts since last report run = $($ClosedAlerts.Count)" $ADFSMessage += "#==================================================" $ADFSMessage += "#" $ADFSMessage += "# Total OPEN alerts" $ADFSMessage += "#==================" $ADFSMessage += "# MEX endpoint alerts = $($ADFSMEX.Count)" $ADFSMessage += "# SQL related issues = $($ADFSSQL.Count)" $ADFSMessage += "# Token related issues = $($ADFSToken.Count)" $ADFSMessage += "# SAML related issues = $($ADFSToken.Count)" $ADFSMessage += "# FsProxy related issues = $($ADFSFsProxy.Count)" $ADFSMessage += "#===============================================" $ADFSMessage += "#" $ADFSMessage += "# Total CLOSED alerts" $ADFSMessage += "#==================" $ADFSMessage += "# MEX endpoint alerts = $($ClosedADFSMEX.Count)" $ADFSMessage += "# SQL related issues = $($ClosedADFSSQL.Count)" $ADFSMessage += "# Token related issues = $($ClosedADFSToken.Count)" $ADFSMessage += "# SAML related issues = $($ClosedADFSSAML.Count)" $ADFSMessage += "# FsProxy related issues = $($ClosedADFSFsProxy.Count)" $ADFSMessage += "#===============================================" $ADFSMessage += "#" $ADFSMessage += "# ADFS OPEN alert detail" $ADFSMessage += "#========================================================"
if ( $OpenADFSAlertsSinceReport.Count -eq 0 ) { $ADFSMessage += "ZERO OPEN ADFS alerts servers for date ($date)." $ADFSMessage += "" }
if ( $OpenADFSAlertsSinceReport.Count -gt 0 ) { if ( $OpenADFSAlertsSinceReport.Count -lt 10 ) { $ADFSMessage += $OpenADFSAlertsSinceReport | sort NetbiosComputerName | select NetbiosComputerName,TimeRaised | out-string $ADFSMessage += "" } if ( $OpenADFSAlertsSinceReport.Count -ge 20 ) { $ADFSMessage += "The number of OPEN ADFS alerts is greater than 20" $ADFSMessage += ""
if ( $ADFSMEX.Count -gt 0 ) { $ADFSMessage += "# MEX endpoint alert detail" $ADFSMessage += "#===============================================" $ADFSMessage += $ADFSMEX $ADFSMessage += "#===============================================" $ADFSMessage += "" }
if ( $ADFSSQL.Count -gt 0 ) { $ADFSMessage += "# SQL alert detail" $ADFSMessage += "#===============================================" $ADFSMessage += $ADFSSQL.Count $ADFSMessage += "#===============================================" $ADFSMessage += "" }
if ( $ADFSToken.Count -gt 0 ) { $ADFSMessage += "# Token alert detail" $ADFSMessage += "#===============================================" $ADFSMessage += $ADFSToken.Count $ADFSMessage += "#===============================================" $ADFSMessage += "" }
if ( $ADFSSAML.Count -gt 0 ) { $ADFSMessage += "# SAML alert detail" $ADFSMessage += "#===============================================" $ADFSMessage += $ADFSSAML $ADFSMessage += "#===============================================" $ADFSMessage += "" }
if ( $ADFSFsProxy.Count -gt 0 ) { $ADFSMessage += "# FsProxy alert detail" $ADFSMessage += "#===============================================" $ADFSMessage += $ADFSFsProxy $ADFSMessage += "#===============================================" $ADFSMessage += "" }
} }
#$ADFSMessage += $OpenADFSAlertsSinceReport $ADFSMessage += "#========================================================" $ADFSMessage += "" }
$ADFSMessage
$ADFSMessage = $ADFSMessage | out-string
# # If Cleanup needed, array of report monitors
# Reset Monitors Script # Put ps1 in mgmtpacks folder # https://sc.scomurr.com/scom-2012-monitor-reset-cleaning-up-the-environment/ # Download # https://gallery.technet.microsoft.com/SCOM-2012-Batch-reset-63a17534
#Alternate #https://gallery.technet.microsoft.com/scriptcenter/Auto-reset-script-for-d8b775ca
if ( $Test -gt 0 ) { foreach ($MonitorDisplayName in $SCOMCoreMonitors.DisplayName) { $Monitors = @(Get-SCOMMonitor -displayname $MonitorDisplayName)
# Set up monitor objects to reset foreach ($Monitor in $Monitors) { $MonitorClass = Get-SCOMClass -Id $Monitor.Target.Id $ActiveMonitors = Get-SCOMClassInstance -Class $MonitorClass | ? { ($_.healthstate -ne 'Success') -AND ( $_.healthstate -ne 'Uninitialized') -AND ($_.IsAvailable -eq $true) } write-host "Found" + $ActiveMonitors.Count + "active monitors." if ( $ActiveMonitors -ne $null) { foreach ($ActiveMonitor in $ActiveMonitors) { write-host " Resetting Health State on '" +$ActiveMonitor.FullName + "'" $ActiveMonitor.ResetMonitoringState($Monitor.ID) } } } } }
# # (Rules) Alerts to auto close after reset monitor
# Resolve alerts after report built $SCOMCoreRuleAlerts | Resolve-SCOMAlert
# Log $Summary = "$ADFSMessage `n `nDisclaimer: This daily summary report is to document the manual tasks used by NOSC to check AD and AD Replication health, Pending Reboot Report, Logical Disk errors,Expiring Certificates, Unhealthy Server/Agents, Unhealthy agent workflow/script alerts, and Servers in Maintenance Mode"
if ( $ADFSAlerts.count -gt 0 ) { $bag.AddValue('Count',$Test) $bag.AddValue('Message',$DSMessage) $bag.AddValue('Summary',$Summary)
$momapi.LogScriptEvent($ScriptName,$EventID,0,"$Summary") }
if ( $ADFSAlerts.count -eq 0 ) { $Message += "ZERO Active Directory Federation Services 2016 alerts for ($date)." $momapi.LogScriptEvent($ScriptName,$EventID,0,"`nZERO Active Directory Federation Services 2016 alerts for ($date).")
$bag.AddValue('Count',$Test) $bag.AddValue('Message',$DSMessage) $bag.AddValue('Summary',$Summary) }
# Return all bags $bag
# Log $Summary = "$ADFSMessage `n `nDisclaimer: This daily summary report is to document the manual tasks used by NOSC to check AD and AD Replication health, Pending Reboot Report, Logical Disk errors,Expiring Certificates, Unhealthy Server/Agents, Unhealthy agent workflow/script alerts, and Servers in Maintenance Mode"
# End of script section #================================================================================= #Log an event for script ending and total execution time. $EndTime = Get-Date $ScriptTime = ($EndTime - $StartTime).TotalSeconds $momapi.LogScriptEvent($ScriptName,$EventID,0,"`n Script Completed. `n Script Runtime: ($ScriptTime) seconds.") #================================================================================= # End of script

